companies = companies.uniq
additional_data = []
companies.each do |company|
begin
	url = Google::Search::Web.new(:query => "#{company} site:http://www.moneyhouse.ch/u/").first.uri.gsub("/pub/","/")
	doc = Nokogiri::HTML(open(url))
	title = doc.css("h1").text.gsub("The page is loading...","").gsub("La page en cours de chargement...","").gsub("Die Seite wird geladen...","")
rescue
	title = company
end
	puts "#{company}, #{title}, #{url}"
	additional_data << [company, title, url]
end

CSV.open("additional_data.csv", "wb") do |csv|
	additional_data.each do |row|
		csv << row
	end
end